jQuery(document).ready(function () {
	jQuery('body').append('<div id="menuOverlay" style="display: none;"></div>');
	jQuery(".parent i").click(toggleMobileNavLink);
	jQuery("#nav-icon4").click(toggleMobileMenu);
	jQuery("#menuOverlay").click(toggleMobileMenu);
});

//Tabs UI JS
$(document).ready(function () {
	var panes = $('.tab-pane');
	var line = $('.tabs .line');

	$('.tab-item').click(function () {
		var index = $(".tab-item").index(this);
		var pane = panes[index];

		$('.tab-item.active').removeClass('active');
		$('.tab-pane.active').removeClass('active');

		$(this).addClass('active');
		pane.classList.add('active');
	});
});

function toggleMobileMenu() {
	var element = jQuery(this);
	if (jQuery("#nav-icon4").hasClass("open")) {
		jQuery("#nav-icon4").toggleClass("open");
		jQuery(".nav-main").animate({
			"left": "-320px"
		}, 500)
		hideOverlay();
		jQuery("body").css("overflow", "auto");
	}
	else {
		jQuery("#nav-icon4").toggleClass("open");
		jQuery(".nav-main").animate({
			"left": "0"
		}, 500);
		showOverlay();
		jQuery("body").css("overflow", "hidden");
	}
}

function showOverlay() {
	jQuery("div#menuOverlay").stop().fadeIn("fast");
}

function hideOverlay() {
	jQuery("div#menuOverlay").stop().fadeOut("fast");
}

function toggleMobileNavLink(e) {
	e.preventDefault();
	var element = jQuery(this);
	var open_class = "open";
	var expand_icon = "icon_accoridion_expand";
	var collapsed_icon = "icon_accoridion_collapse";

	if (element.siblings(".mobile-dropdown").hasClass("open")) {
		element.siblings(".mobile-dropdown").removeClass(open_class);
		element.removeClass(collapsed_icon).addClass(expand_icon);
		element.siblings(".mobile-dropdown").slideUp();
	}
	else {
		element.siblings(".mobile-dropdown").addClass(open_class);
		element.removeClass(expand_icon).addClass(collapsed_icon);
		element.siblings(".mobile-dropdown").slideDown();
	}
}

function showLogin() {
    $("#dOverlay").show();
    $("#dLogin").show();
}

function hideLogin() {
    $("#dOverlay").hide();
    $("#dLogin").hide();
}

